(4954, 333, 'physician_referrals_text', 'Physician Referrals'),
(4955, 333, '_physician_referrals_text', 'field_5a58d74705226'),
(4956, 333, 'physician_referrals_paragraph', '<div class=\"row\">\r\n<div class=\"col-sm-12\">\r\n<div class=\"title-form\">\r\n<h1>Physician Referrals</h1>\r\nWe gladly accept patients from referring physicians for Neurological, Geriatric, Speech, Ayurvedic and Neuropsychological assessments. Please download our<a href=\"http://103.223.184.130/~dandenon/wp-content/uploads/2018/01/ReferralForm-Revised.pdf\" target=\"_blank\" rel=\"noopener\"><u> physician referral form</u></a> and fax it to (03) 9701 3036. If you require assistance or if the patient’s condition is urgent, please contact our medical staff for immediate attention at (03) 9791 1599.\r\n\r\n</div>\r\n<div class=\"refrel-footer\">\r\n<h3>Hospital Admissions</h3>\r\nOur physicians have admitting privileges at Box Hill Hospital, Frankston Hospital, Jessie McPherson Private Hospital and Monash Medical Centre. We promptly notify the referring physician of the results of our tests and if hospital admission becomes necessary.\r\n\r\n</div>\r\n</div>\r\n</div>'),
(4957, 333, '_physician_referrals_paragraph', 'field_5a58d75405227'),
